mapper.select:
select d.*,c.name from dish d left outer join sky_take_out.category c on d.category_id = c.id


        and d.name like concat('%',#{name},'%')


        and d.category_id=#{id}


        and d.status=#{status}


order by d.create_time desc

mapper.update:
update dish


        and name=#{name}


        and category_id=#{categoryId}


        and price=#{price}


        and image=#{image}


        and description=#{description}


        and status=#{status}


where id=#{id}
